function GameStatusView(props) {
    if (props.gameComplete) {
        return <>
            <div>Игра завершена !</div>
            {props.turnNo - 1 >=5?
                <div>За  {props.turnNo - 1}  ходов</div>:
                <div>За  {props.turnNo - 1}  хода</div>
            }

            <div><button className='Game-button' onClick={props.onShowNumCardsSelection}>Сиграть снова?</button></div>
        </>;
    } else {
        return <>
            Перевернуто карт: {props.turnNo}   Найдено пар: {props.pairsFound}
            <button className='Game-button' onClick={props.onShowNumCardsSelection}>Новая игра</button>
        </>;
    }
}

export default GameStatusView;
